mapper.update:
UPDATE CART SET USER_ID=#{user_id},  BOOK_NO=#{book_no}, CNT=#{cnt}, 
STORE_CODE=#{store_code} 
WHERE CART_IDX=#{cart_idx}

mapper.select:
SELECT COUNT(*) FROM CART 
WHERE BOOK_NO = #{book_no}
AND USER_ID= #{id}
AND STORE_CODE= #{store_code}

SELECT CART_IDX FROM CART
WHERE BOOK_NO = #{book_no}
AND USER_ID= #{id}
AND STORE_CODE= #{store_code}

SELECT * FROM CART
WHERE USER_ID = #{id}
AND STORE_CODE = 'online'

SELECT * FROM CART C, BOOK B
WHERE C.BOOK_NO = B.BOOK_NO
AND C.USER_ID= #{id}

AND STORE_CODE = #{store_code}


AND STORE_CODE <> 'online'

mapper.delete:
DELETE FROM CART 
WHERE STORE_CODE <> 'online'
OR STORE_CODE <> #{store_code}
AND USER_ID = #{id}

mapper.update:
UPDATE CART
SET CNT = #{cnt}
WHERE CART_IDX = #{cart_idx}

mapper.select:
SELECT DISTINCT STORE_CODE  FROM CART, 
WHERE STORE_CODE != 'online'
AND USER_ID= #{id}

mapper.delete:
DELETE CART
WHERE STORE_CODE = #{store_code }
AND USER_ID=#{id}
