mapper.select:
SELECT DISTINCT CATEGORY_MAIN
FROM BOOK_CATEGORY
WHERE CATEGORY_CODE = #{category_code}

SELECT DISTINCT CATEGORY_NAME
FROM BOOK_CATEGORY
WHERE CATEGORY_MAIN = #{string}

SELECT BOOK_NO, BOOK_NAME, BOOK_IMG FROM BOOK WHERE 1 = 1
BOOK_NO = #{book_no}
AND 1 = 2

mapper.update:
UPDATE QNA
SET QNA_TITLE = #{qna_title},
	QNA_CONTENT = #{qna_content},
	QNA_SECRET = #{qna_secret},
	QNA_FILE_NAME = #{qna_file_name}
WHERE QNA_NO = #{qna_no}

UPDATE QNA
SET QNA_COUNT = QNA_COUNT + 1
WHERE QNA_NO = #{qna_no}
