mapper.select:
SELECT * FROM GOODS
	WHERE GOODS_START_DATE <= #{nowDate}
	AND GOODS_END_DATE >= #{nowDate}

SELECT C.CART_IDX, C.USER_ID, C.CNT, C.STORE_CODE, B.BOOK_NAME, B.BOOK_IMG, B.BOOK_NO, B.BOOK_SALEPRICE, BC.CATEGORY_CODE, BC.CATEGORY_MAIN, BC.CATEGORY_MIDDLE, BC.CATEGORY_NAME FROM CART C, BOOK B, BOOK_CATEGORY BC WHERE B.BOOK_NO = C.BOOK_NO AND BC.CATEGORY_NO = B.CATEGORY_NO AND (
CART_IDX = #{cart_no}
)

mapper.update:
UPDATE USERS
SET ZIP_CODE = #{zip_code},
STREET_ADDR = #{street_addr},
REMAINING_ADDR = #{remaining_addr}
WHERE ID = #{id}

mapper.select:
SELECT MAX(ORDER_NO)
FROM ORDERS
WHERE ORDER_USER_ID = #{id}

update.foreach:
UPDATE BOOK_STOCK
SET CNT = CNT - #{list.cnt}
WHERE BOOK_NO = #{list.book_no} AND STORE_CODE = #{order_office_no}
